import React, { Component } from 'react';
import './App.css';
import Header from './components/Header';
import Search from './components/Search';
import Users from './components/Users';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      username: '',
      image: '',
      bio:'',
      list:[]
    }
  } 

  componentDidMount(){ 
    //eslint-disable-next-line
    console.log(this.props);
    const { match: {params} } = this.props;
    axios.get(`https://api.github.com/users/${params.username}`)
    .then(data => this.setState(() => ({ //despues de que se conecto al servidor, obtenemos los datos
        username: data.login, //projects.data es un array, esto es de axios
        image: data.avatar_url,
        bio: data.bio
    }))
    ).catch(err => console.log(err.message)); //eslint-disable-line
  }

  render() {
    return (
      <div>
        <Header />
        <div className="container">
          <Search onSubmitUsername={(username) => {
            console.log(username)
          }}/>
          <br />
          <Users username/> 
        </div>
      </div>
    );
  }
}

export default App;
