import React, {Component} from 'react';
import axios from 'axios';
import PropTypes from 'prop-types'; 

class List extends Component { //herencia
    constructor(props) { 
        super(props); 
        this.state = { user: [] }; 
    } 

    componentDidMount(){ //llamados al backend
        //eslint-disable-next-line
        console.log(this.props);
        const { match: {params} } = this.props;
        axios.get(`https://api.github.com/users/${params.username}`)
        .then(user => this.setState(() => ({ //despues de que se conecto al servidor, obtenemos los datos
            user: user.data //projects.data es un array, esto es de axios
        }))
        ).catch(err => console.log("User not found")); //eslint-disable-line
    }

    render(){
        return (
            <div>
                <div className="container list">
                    <section className="eight offset-by-two columns"> 
                        <h4>
                            <br/>
                            <br/>
                                {this.state.user.map((data) =>
                                    <li key={data.id}>
                                        {data.name}
                                    </li>
                            )}
                        </h4>
                    </section>
                </div>
            </div>
        )
    }
}

List.propTypes = {
    match: PropTypes.object.isRequired
}

export default List;
