import React from 'react'
import {Form, Button, FormGroup, FormControl } from 'react-bootstrap';
import PropTypes from 'prop-types';
import Users from './Users';
import axios from 'axios';

const Search = ({ onSubmitUsername }) => {
  const user = "";
    const handleSubmit = e => {
    e.preventDefault(); //evitar que el navegador intente llamar a un servidor al momento de darle submit al formulario
    const username = e.target.username.value;
    this.user = username;
    if (onSubmitUsername && username) {
      onSubmitUsername(username);
    }
  };

  function componentDidMount(){ //llamados al backend
    //eslint-disable-next-line
    axios.get(`https://api.github.com/users/${user}`)
    .then(user => this.setState(() => ({ //despues de que se conecto al servidor, obtenemos los datos
        user: user.data //projects.data es un array, esto es de axios
    }))
    ).catch(err => console.log("User not found")); //eslint-disable-line
  }
  componentDidMount();
  return (
    <Form inline >
      <FormGroup controlId="formInlineEmail">
        <FormControl type="text" placeholder="Enter username" />
      </FormGroup>{' '}
      <Button type="submit" onSubmit={handleSubmit}>Add</Button>
      <Users match={user} />
    </Form>
  )
}

Search.propTypes = {
  onSubmitUsername: PropTypes.func
};

export default Search;
