import React, { Component } from 'react';
import { Grid, Row, Col, Panel, Media, Button } from 'react-bootstrap';
import axios from 'axios';


class Users extends Component {
  constructor(props) { 
    super(props); 
    this.state = { user: [] }; 
  } 

  componentDidMount(){ //llamados al backend
    //eslint-disable-next-line
    console.log(this.props);
    const { match: {params} } = this.props;
    axios.get(`https://api.github.com/users/${params.username}`)
    .then(user => this.setState(() => ({ //despues de que se conecto al servidor, obtenemos los datos
        user: user.data //projects.data es un array, esto es de axios
    }))
    ).catch(err => console.log("User not found")); //eslint-disable-line
}
  return (){
    return (
      <Grid>
      <Row className="show-grid">
        <Col xs={12} md={8}>
          <Panel>
            <Panel.Body>
              <Media>
                <Media.Left>
                  <img />
                </Media.Left>
                <Media.Body>
                  <Media.Heading>Media Heading</Media.Heading>
                  <p>
                    Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque
                    ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at,
                    tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate
                    fringilla. Donec lacinia congue felis in faucibus.
                  </p>
                </Media.Body>
              </Media>
              <Button>Delete</Button>
            </Panel.Body>
          </Panel>
          <Panel>
            <Panel.Body>
              <Media>
                <Media.Left>
                  <img width={64} height={64} src="https://via.placeholder.com/150" alt="thumbnail" />
                </Media.Left>
                <Media.Body>
                  <Media.Heading>Media Heading</Media.Heading>
                  <p>
                    Cras sit amet nibh libero, in gravida nulla. Nulla vel metus scelerisque
                    ante sollicitudin commodo. Cras purus odio, vestibulum in vulputate at,
                    tempus viverra turpis. Fusce condimentum nunc ac nisi vulputate
                    fringilla. Donec lacinia congue felis in faucibus.
                  </p>
                </Media.Body>
              </Media>
              <Button>Delete</Button>
            </Panel.Body>
          </Panel>
        </Col>
      </Row>
    </Grid>
    )
  }
}
List.propTypes = {
  match: PropTypes.object.isRequired
}

export default Users;
